infoDataConvert.value:
function (info, data) {
        var formData = {};
        formData.formId = info.baseForm.formId;
        formData.applyFormId = info.baseForm.applyFormId;
        formData.moneyType = { id: 0, name: "人民币" }; //币种
        formData.empDepartment = info.baseForm.empDepartment; //部门
        formData.travelInfos = info.travelInfos;
        formData.costCenter = info.baseForm.costCenter;
        formData.costDetailsStays = info.costDetailsStays;
        formData.costDetailsTraffics = info.costDetailsTraffics;
        formData.costDetailsAllowance = info.costDetailsSubsidys;
        formData.costDetailsOthers = info.costDetailsOthers;

        function getCity(name) {
            var citys = data.citys;
            for (var i = 0; i < citys.length; i++) {
                if (citys[i].cityName == name) {
                    return citys[i];
                }
            }
        }

        function getTrafficTool(name) {
            var trafficTools = data.trafficTool;
            for (var i = 0; i < trafficTools.length; i++) {
                if (trafficTools[i].id == name) {
                    return trafficTools[i];
                }
            }
        }

        function getTrafficTool(name) {
            var trafficTools = data.trafficTool;
            for (var i = 0; i < trafficTools.length; i++) {
                if (trafficTools[i].id == name) {
                    return trafficTools[i];
                }
            }
        }

        function getInvoiceType(id) {
            var invoiceTypes = data.costDetailsStays[0].invoiceType;
            for (var i = 0; i < invoiceTypes.length; i++) {
                if (invoiceTypes[i].id == id) {
                    return invoiceTypes[i];
                }
            }
        }

        function getTaxRate(id) {
            var taxRates = data.costDetailsStays[0].taxRate;
            for (var i = 0; i < taxRates.length; i++) {
                if (taxRates[i].id == (id ? parseInt(id) : 0)) {
                    return taxRates[i];
                }
            }
            return null;
        }
        if (info.costOffsets && info.costOffsets.length > 0) { //费用冲销
            formData.costOffsets = [];
            for (var i = 0; i < info.costOffsets.length; i++) {
                var costOffset = info.costOffsets[i];
                formData.costOffsets[i] = {
                    borrowFormNum: costOffset.borrowFormNum, //借款单号
                    borrowAmount: costOffset.borrowAmount, //借款金额
                    unOffsetAmount: costOffset.unOffsetAmount, //未冲销金额
                    stringId: costOffset.isChecked === "0" ? [] : [{ "id": "1" }], //是否选择
                    thisOffsetAmount: costOffset.thisOffsetAmount ? costOffset.thisOffsetAmount + ("元") : 0 //本次冲销金额
                }
            }
        }
        //编辑
        if (info.baseForm.id && info.baseForm.id) {
            var reg = new RegExp("
", "g");
            formData.costDesc = info.baseForm.costDesc.replace(reg, "\n"); //出差事由
            formData.innerRemark = info.baseForm.innerRemark.replace(reg, "\n"); //内部备注
            formData.fileList = info.fileList; //文件上传
            formData.supplementMoney = info.baseForm.applyMoney + '元'; //合计金额
            if (info.approveHistory && info.approveHistory.length > 0) {//审批历史
                formData.approveHistory = []; //费用明细
                var optionObj = {
                    1: "发起流程",
                    2: "审批通过",
                    3: "流程加签",
                    4: "撤回流程",
                    5: "驳回流程",
                    6: "流程跳过",
                    7: "任务认领",
                    8: "暂存待办",
                    9: "回复"
                }
                for (var i = 0; i < info.approveHistory.length; i++) {
                    var approveHistory = info.approveHistory[i];
                    formData.approveHistory[i] = {
                        "assigneeName": approveHistory.assigneeName, //金额
                        "suggestion": approveHistory.suggestion,
                        "thisOptionStr": optionObj[approveHistory.thisOption],
                        "approveTimeStr": new Date(approveHistory.approveTime).toLocaleString()
                    }

                }

            }
        }
        $.each(formData.travelInfos, function(i, v) {
            v.startDate = v.startDate ? { startTime: v.startDate } : "";
            v.endDate = v.endDate ? { startTime: v.endDate } : "";
            v.startCity = v.startCity ? getCity(v.startCity) : null;
            v.endCity = v.endCity ? getCity(v.endCity) : null;
        });
        $.each(formData.costDetailsStays, function(i, v) {
            v.costHappenDate = v.costHappenDate ? { startTime: v.costHappenDate } : "";
            v.startDate = v.startDate ? { startTime: v.startDate } : "";
            v.endDate = v.endDate ? { startTime: v.endDate } : "";
            v.travelCity = v.travelCity ? getCity(v.travelCity) : null;
            v.invoiceType = v.invoiceType ? getInvoiceType(v.invoiceType) : null;
            v.taxRate = v.taxRate ? getTaxRate(v.taxRate) : null;
        });
        $.each(formData.costDetailsOthers, function(i, v) {
            v.costHappenDate = v.costHappenDate ? { startTime: v.costHappenDate } : "";
        });
        $.each(formData.costDetailsTraffics, function(i, v) {
            v.costHappenDate = v.costHappenDate ? { startTime: v.costHappenDate } : "";
            v.startCity = v.startCity ? getCity(v.startCity) : null;
            v.endCity = v.endCity ? getCity(v.endCity) : null;
            v.trafficTool = v.trafficTool ? getTrafficTool(v.trafficTool) : null;

        });


        return formData;
    }

selectDataConvert.value:
function (data) {
    var trafficTool = [];
    if (data.vehicleList && data.vehicleList.length > 0) {
        for (var i = 0; i < data.vehicleList.length; i++) {
            trafficTool.push({
                name: data.vehicleList[i].mode,
                id: data.vehicleList[i].mode

            })
        }

    }
    var citys = data.cityList;
    return {
        //交通工具
        costDetailsTraffics: [{
            "trafficTool": trafficTool
        }],
        "trafficTool": trafficTool,
        //税率
        costDetailsStays: [{
            invoiceType: [
                { id: "0", name: "增值税专用发票" },
                { id: "1", name: "普通发票" },
                { id: "2", name: "后补发票" },
                { id: "3", name: "无票报销" },
                { id: "4", name: "电子普通发票" }
            ],
            taxRate: [
                { id: "3", name: "3%" },
                { id: "6", name: "6%" },
                { id: "11", name: "11%" },
                { id: "13", name: "13%" },
                { id: "17", name: "17%" }
            ]
        }],
        citys: citys,
        //预算查看
        "checkYS": function(values) {
            var serchTemp = [];
            var costCenter = values.costCenter,
                clfType = "010";
            if (values.travelInfos && values.travelInfos.length > 0) {
                for (var i = 0; i < values.travelInfos.length; i++) {
                    var cD = values.travelInfos[i];
                    var cdTemp = { costDetailType: clfType, "costCenter": costCenter };
                    if (cD.startDate && cD.startDate.startTime && cD.startDate.startTime.length > 0) {
                        cdTemp.month = cD.startDate.startTime.substr(5, 2);
                        cdTemp.year = cD.startDate.startTime.substring(0, 4);
                    } else {
                        continue;
                    }
                    serchTemp.push(cdTemp);
                }
            }

            if (values.costDetailsTraffics && values.costDetailsTraffics.length > 0) {
                for (var i = 0; i < values.costDetailsTraffics.length; i++) {
                    var cD = values.costDetailsTraffics[i];
