                    if (cD.costHappenDate && cD.costHappenDate.startTime && cD.costHappenDate.startTime.length > 0) {
                        cdTemp.month = cD.costHappenDate.startTime.substr(5, 2);
                        cdTemp.year = cD.costHappenDate.startTime.substring(0, 4);
                    } else {
