            }
            if (values.costDetailsStays && values.costDetailsStays.length > 0) {
                for (var i = 0; i < values.costDetailsStays.length; i++) {
                    var cD = values.costDetailsStays[i];
                    var cdTemp = { costDetailType: clfType, "costCenter": costCenter };
                    if (cD.costHappenDate && cD.costHappenDate.startTime && cD.costHappenDate.startTime.length > 0) {
                        cdTemp.month = cD.costHappenDate.startTime.substr(5, 2);
                        cdTemp.year = cD.costHappenDate.startTime.substring(0, 4);
                    } else {
                        continue;
                    }
                    serchTemp.push(cdTemp);
                }
            }
            if (values.costDetailsOthers && values.costDetailsOthers.length > 0) {
                for (var i = 0; i < values.costDetailsOthers.length; i++) {
                    var cD = values.costDetailsOthers[i];
                    var cdTemp = { costDetailType: clfType, "costCenter": costCenter };
