                }
            }
            return serchTemp;

        },
    };
}

saveDataConvert.value:
function (info, data) {
    var costDetailType = info.costDetailType ? info.costDetailType : "010";
    var travelInfosTemp = [];
    var costDetailsStaysTemp = [];
    var costDetailsTrafficsTemp = [];
    var costDetailsAllowanceTemp = [];
    var costDetailsOthersTemp = [];

    if (!info.costOffsets || info.costOffsets.stringId == "") {
        info.costOffsets = null;
    } else {
        info.costOffsets.thisOffsetAmount = info.costOffsets.thisOffsetAmount.replace("元", "")
    }
    $.each(info.travelInfos, function(i, v) {
        travelInfosTemp.push({
            "startDate": v.startDate ? v.startDate.startTime : "",
            "endDate": v.endDate ? v.endDate.startTime : "",
            "startCity": v.startCity ? v.startCity.cityName : "",
            "endCity": v.endCity ? v.endCity.cityName : "",
            "travelDays": v.travelDays
        });
    });
    $.each(info.costDetailsStays, function(i, v) {
        costDetailsStaysTemp.push({
            "costHappenDate": v.costHappenDate ? v.costHappenDate.startTime : "",
            "startDate": v.startDate ? v.startDate.startTime : "",
            "endDate": v.endDate ? v.endDate.startTime : "",
            "travelCity": v.travelCity ? v.travelCity.cityName : "",
            "travelDays": v.travelDays,
            "stayStandard": v.stayStandard,
            "amount": v.amount ? v.amount : "0",
            "invoiceType": v.invoiceType.id,
            "taxRate": v.taxRate ? v.taxRate.id : "0",
            "taxMoney": v.taxMoney,
            "costDetailType": costDetailType,
            "costdetailDiv": 1
        });
    });
    $.each(info.costDetailsTraffics, function(i, v) {
        costDetailsTrafficsTemp.push({
            "costHappenDate": v.costHappenDate ? v.costHappenDate.startTime : "",
            "startCity": v.startCity ? v.startCity.cityName : "",
            "endCity": v.endCity ? v.endCity.cityName : "",
            "trafficTool": v.trafficTool ? v.trafficTool.name : "",
            "amount": v.amount ? v.amount : "0",
            "costDetailType": costDetailType,
            "costdetailDiv": 2
        });
    });
    $.each(info.costDetailsAllowance, function(i, v) {
        costDetailsAllowanceTemp.push({
            "costHappenDate": info.travelInfos[i].startDate ? info.travelInfos[i].startDate.startTime : "",
            "travelCity": info.travelInfos[i].endCity ? info.travelInfos[i].endCity.cityName : "",
            "travelDays": v.travelDays ? v.travelDays : 0,
            "amount": v.amount ? v.amount : "0",
            "costDetailType": costDetailType,
            "costdetailDiv": 4
        });
    });
    $.each(info.costDetailsOthers, function(i, v) {
        costDetailsOthersTemp.push({
            "costHappenDate": v.costHappenDate ? v.costHappenDate.startTime : "",
            "amount": v.amount ? v.amount : "0",
            "costDetailType": costDetailType,
            "costdetailDiv": 3
        });
    });
    return {
        baseForm: JSON.stringify({
            formId: info.formId,
            applyFormId: info.applyFormId,
            moneyType: info.moneyType.id,
            costCenter: info.costCenter,
            costDesc: info.costDesc.replace(/\n|\r\n/g, "
"),
            innerRemark: info.innerRemark.replace(/\n|\r\n/g, "
"),
            applyMoney: info.applyMoney.replace("元", "") ? info.applyMoney.replace("元", "") : 0,
        }),
        travelInfos: JSON.stringify(travelInfosTemp ? travelInfosTemp : []),
        costDetailsStays: JSON.stringify(costDetailsStaysTemp ? costDetailsStaysTemp : []),
        costDetailsTraffics: JSON.stringify(costDetailsTrafficsTemp ? costDetailsTrafficsTemp : []),
        costDetailsAllowance: JSON.stringify(costDetailsAllowanceTemp ? costDetailsAllowanceTemp : []),
        costDetailsOthers: JSON.stringify(costDetailsOthersTemp ? costDetailsOthersTemp : []),
        costOffsets: JSON.stringify(info.costOffsets ? [info.costOffsets] : []),
        fileList: JSON.stringify(info.fileList ? info.fileList : [])
    }
}
