infoDataConvert.value:
function (info, data) {
    var formData = {};
    formData.formId = info.baseForm ? info.baseForm.formId : null;
    formData.moneyType = { id: 0, name: "人民币" }; //币种
    formData.empDepartment = info.baseForm ? info.baseForm.empDepartment : null; //部门
    formData.costDetailType = info.costDetailType ? info.costDetailType : "010";

    function getCity(name) {
        var citys = data.citys;
        for (var i = 0; i < citys.length; i++) {
            if (citys[i].cityName == name) {
                return citys[i];
            }
        }
    }
    //编辑
    if (info.baseForm && info.baseForm.id) {
        var reg = new RegExp("
", "g");
        formData.costDesc = info.baseForm.costDesc.replace(reg, "\n"); //增补原因
        formData.innerRemark = info.baseForm.innerRemark.replace(reg, "\n"); //内部备注
        formData.fileList = info.fileList; //文件上传
        console.log(info);
        formData.applyMoney = info.baseForm.applyMoney + '元'; //合计金额
        if (info.costDetails && info.costDetails.length > 0) {
            formData.costDetails = []; //费用明细

            for (var i = 0; i < info.costDetails.length; i++) {
                var costDetail = info.costDetails[i];
                formData.costDetails[i] = {
                    "amount": costDetail.amount, //金额
                    "startCity": costDetail.startCity ? getCity(costDetail.startCity) : null,
                    "endCity": costDetail.endCity ? getCity(costDetail.endCity) : null,
                    "trafficTool": costDetail.trafficTool ? {
                        id: costDetail.trafficTool,
                        name: costDetail.trafficTool
                    } : null,
                    "costdetaildi": 4,
                    "budgetStatus": "未超预算",
                    "costHappenDate": { startTime: costDetail.costHappenDate } //费用发生月份
                }

            }

        }
        if (info.travelInfos && info.travelInfos.length > 0) {
            formData.travelInfos = []; //费用明细

            for (var i = 0; i < info.travelInfos.length; i++) {
                var travelInfo = info.travelInfos[i];
                formData.travelInfos[i] = {
                    "amount": travelInfo.amount, //金额
                    "startCity": travelInfo.startCity ? getCity(travelInfo.startCity) : null,
                    "endCity": travelInfo.endCity ? getCity(travelInfo.endCity) : null,

                    "costdetaildi": 4,
                    "budgetStatus": "未超预算",
                    "travelDays": travelInfo.travelDays,
                    "stayStandard": travelInfo.stayStandard,
                    "allowanceAmount": travelInfo.allowanceAmount,
                    "stayAmount": travelInfo.stayAmount,
                    "otherAmount": travelInfo.otherAmount,
                    "totalAmount": travelInfo.totalAmount,
                    "startDate": { startTime: travelInfo.startDate },
                    "endDate": { startTime: travelInfo.endDate }
                }

            }

        }
        if (info.approveHistory && info.approveHistory.length > 0) {
            formData.approveHistory = []; //费用明细
            var optionObj = {
                1: "发起流程",
                2: "审批通过",
                3: "流程加签",
                4: "撤回流程",
                5: "驳回流程",
                6: "流程跳过",
                7: "任务认领",
                8: "暂存待办",
                9: "回复"
            }
            for (var i = 0; i < info.approveHistory.length; i++) {
                var approveHistory = info.approveHistory[i];
                formData.approveHistory[i] = {
                    "assigneeName": approveHistory.assigneeName, //金额
                    "suggestion": approveHistory.suggestion,
                    "thisOptionStr": optionObj[approveHistory.thisOption],
                    "approveTimeStr": new Date(approveHistory.approveTime).toLocaleString()
                }

            }

        }
    } else {
        return formData;
    }
    return formData;
}

selectDataConvert.value:
function (data) {
        var trafficTool = [];
        if (data.vehicleList && data.vehicleList.length > 0) {
            for (var i = 0; i < data.vehicleList.length; i++) {
                trafficTool.push({
                    name: data.vehicleList[i].mode,
                    id: data.vehicleList[i].mode

                })
            }

        }
        var citys = data.cityList;
        return {
            //交通工具
            costDetails: [{
                trafficTool: trafficTool
            }],
            citys: citys,
            //预算查看
            "checkYS": function(values) {
                var serchTemp = [];
                if (values.costDetails && values.costDetails.length > 0) {
                    for (var i = 0; i < values.costDetails.length; i++) {
                        var cD = values.costDetails[i];
                        var cdTemp = { costDetailType: "010" };
                        if (cD.costHappenDate && cD.costHappenDate.startTime && cD.costHappenDate.startTime.length > 0) {
                            cdTemp.month = cD.costHappenDate.startTime.substr(5, 2);
                            cdTemp.year = cD.costHappenDate.startTime.substring(0, 4);
                        } else {
                            continue;
                        }
                        serchTemp.push(cdTemp);
                    }
                }
                if (values.travelInfos && values.travelInfos.length > 0) {
                    for (var i = 0; i < values.travelInfos.length; i++) {
                        var cD = values.travelInfos[i];
                        var cdTemp = { costDetailType: "010" };
                        if (cD.startDate && cD.startDate.startTime && cD.startDate.startTime.length > 0) {
                            cdTemp.month = cD.startDate.startTime.substr(5, 2);
                            cdTemp.year = cD.startDate.startTime.substring(0, 4);
                        } else {
                            continue;
                        }
                        serchTemp.push(cdTemp);
                    }
                }
                return serchTemp;

            },
        };
    }

saveDataConvert.value:
function (info, data) {
        var costDetailTemp = [],
            travelInfosTemp = [];
        var costDetailType = info.costDetailType ? info.costDetailType : "010";
        $.each(info.costDetails, function(i, v) {
            costDetailTemp.push({
                "costHappenDate": v.costHappenDate ? v.costHappenDate.startTime.substring(0, 10) : 0,
                "startCity": v.startCity ? v.startCity.cityName : "",
                "endCity": v.endCity ? v.endCity.cityName : "",
                "amount": v.amount ? v.amount : 0,
                "trafficTool": v.trafficTool ? v.trafficTool.name : "",
                "budgetStatus": v.budgetStatus == "未超预算" ? "0" : "1",
                "costdetailDiv": "2",
                "costDetailType": costDetailType
            });
        });


        $.each(info.travelInfos, function(i, v) {
            travelInfosTemp.push({
                "startDate": v.startDate ? v.startDate.startTime.substring(0, 10) : 0,
                "endDate": v.endDate ? v.endDate.startTime.substring(0, 10) : 0,
                "startCity": v.startCity ? v.startCity.cityName : "",
                "endCity": v.endCity ? v.endCity.cityName : "",
                "travelDays": v.travelDays ? v.travelDays : 0,
                "stayStandard": v.stayStandard ? v.stayStandard : 0,
                "stayAmount": v.stayAmount ? v.stayAmount : 0,
                "allowanceAmount": v.allowanceAmount ? v.allowanceAmount : 0,
                "otherAmount": v.otherAmount ? v.otherAmount : 0,
                "totalAmount": v.totalAmount ? v.totalAmount : 0,
                "budgetStatus": v.budgetStatus == "未超预算" ? "0" : "1",
                "costDetailType": costDetailType
            });
        });
        return {
            baseForm: JSON.stringify({
                formId: info.formId,
                moneyType: info.moneyType.id,
                costDesc: info.costDesc.replace(/\n|\r\n/g, "
"),
                innerRemark: info.innerRemark.replace(/\n|\r\n/g, "
"),
                applyMoney: info.costDetailAllMoney.replace("元", "") ? info.costDetailAllMoney.replace("元", "") : 0,
            }),
            travelInfos: JSON.stringify(travelInfosTemp ? travelInfosTemp : []),
            costDetails: JSON.stringify(costDetailTemp ? costDetailTemp : []),
            fileList: JSON.stringify(info.fileList ? info.fileList : [])
        }
    }
