infoDataConvert.value:
function (info, data) {
        var formData = {};
        formData.formId = info.baseForm.formId; //标识
        formData.moneyType = { id: 0, name: "人民币" }; //币种
        formData.empDepartment = info.baseForm.empDepartment; //部门
        if (info.costOffsets && info.costOffsets.length > 0) { //费用冲销
            formData.costOffsets = [];
            for (var i = 0; i < info.costOffsets.length; i++) {
                var costOffset = info.costOffsets[i];
                formData.costOffsets[i] = {
                    borrowFormNum: costOffset.borrowFormNum, //借款单号
                    borrowAmount: costOffset.borrowAmount, //借款金额
                    unOffsetAmount: costOffset.unOffsetAmount, //未冲销金额
                    stringId: costOffset.isChecked === "0" ? [] : [{ "id": "1" }], //是否选择
                    thisOffsetAmount: costOffset.thisOffsetAmount ? costOffset.thisOffsetAmount + ("元") : 0 //本次冲销金额
                }
            }
        }
        var costDetailTypeObj = {};
        if (data.costDetailType && data.costDetailType.length > 0) {
            for (var i = 0; i < data.costDetailType.length; i++) {
                var cdt = data.costDetailType[i];
                costDetailTypeObj[cdt.code] = cdt
            }
        }
        if (info.baseForm.id && info.baseForm.id) { //编辑
            var reg = new RegExp("
", "g");
            formData.costDesc = info.baseForm.costDesc.replace(reg, "\n"); //费用描述
            formData.innerRemark = info.baseForm.innerRemark.replace(reg, "\n"); //内部备注
            formData.fileList = info.fileList; //文件上传
            if (info.costDetails && info.costDetails.length > 0) {
                formData.costDetail = [];
                for (var i = 0; i < info.costDetails.length; i++) {
                    var costDetail = info.costDetails[i];
                    formData.costDetail[i] = {
                        "amount": costDetail.amount,
                        "costDetailType": costDetail.costDetailType ? {
                            id: costDetail.costDetailType,
                            name: costDetailTypeObj[costDetail.costDetailType].name + "(" + costDetailTypeObj[costDetail.costDetailType].code + ")"
                        } : null,
                        "costdetaildi": 3,
                        "costHappenDate": costDetail.costHappenDate
                    }

                }
            }
            if (info.approveHistory && info.approveHistory.length > 0) {//审批历史
                formData.approveHistory = []; //费用明细
                var optionObj = {
                    1: "发起流程",
                    2: "审批通过",
                    3: "流程加签",
                    4: "撤回流程",
                    5: "驳回流程",
                    6: "流程跳过",
                    7: "任务认领",
                    8: "暂存待办",
                    9: "回复"
                }
                for (var i = 0; i < info.approveHistory.length; i++) {
                    var approveHistory = info.approveHistory[i];
                    formData.approveHistory[i] = {
                        "assigneeName": approveHistory.assigneeName, //金额
                        "suggestion": approveHistory.suggestion,
                        "thisOptionStr": optionObj[approveHistory.thisOption],
                        "approveTimeStr": new Date(approveHistory.approveTime).toLocaleString()
                    }

                }

            }
        } else {
            return formData;
        }
        return formData;
    }

selectDataConvert.value:
function (data) {
        var cdtTemp = [];
        for (var i = 0, l = data.costDetailType.length; i < l; i++) {
            cdtTemp.push({
                id: data.costDetailType[i].code,
                name: data.costDetailType[i].name + "(" + data.costDetailType[i].code + ")"
            })
        }
        return {
            costDetail: [{
                costDetailType: cdtTemp

            }],
            //预算查看
            "checkYS": function(values) {
                var serchTemp = [];
                if (values.costDetail && values.costDetail.length > 0) {
                    for (var i = 0; i < values.costDetail.length; i++) {
                        var cD = values.costDetail[i];
                        var cdTemp = {};
                        if (cD.costDetailType &&
                            cD.costDetailType[0] &&
                            cD.costDetailType[0].id.length > 0) {
                            cdTemp.costDetailType = cD.costDetailType[0].id;
                        } else {
                            continue;
                        }
                        if (cD.costHappenDate && cD.costHappenDate.startTime && cD.costHappenDate.startTime.length > 0) {
                            var sTime = new Date(cD.costHappenDate.startTime);
                            var month = sTime.getMonth() + 1;
                            cdTemp.year = sTime.getFullYear();
                            cdTemp.month = (month < 10 ? "0" + month : month)
                        } else {
                            continue;
                        }
                        //cdTemp.costCenter = "10.00.78.00.5"; //调试用 暂时写死
                        serchTemp.push(cdTemp);

                    }

                }
                return serchTemp;

            },
        };
    }

saveDataConvert.value:
function (info, data) {
        var costDetailTemp = [];
        $.each(info.costDetail, function(i, v) {
            costDetailTemp.push({
                "amount": v.amount ? v.amount : 0,
                "costDetailType": v.costDetailType.id,
                "costdetaildiv": 3,
                "costHappenDate": v.costHappenDate.startTime ? v.costHappenDate.startTime.substring(0, 10) : null
            });
        });
        if (!info.costOffsets || info.costOffsets.stringId == "") {
            info.costOffsets = null;
        } else {
            info.costOffsets.thisOffsetAmount = info.costOffsets.thisOffsetAmount.replace("元", "")
        }
        console.log(data);
        console.log(info);
        return {
            baseForm: JSON.stringify({
                formId: info.formId,
                moneyType: info.moneyType.id,
                costDesc: info.costDesc.replace(/\n|\r\n/g, "
"),
                innerRemark: info.innerRemark.replace(/\n|\r\n/g, "
"),
                applyMoney: info.applyMoney.replace("元", ""),
                receiptMoney: info.receiptMoney.replace("元", "")
            }),
            costDetails: JSON.stringify(costDetailTemp ? costDetailTemp : []),

            costOffsets: JSON.stringify((info.costOffsets) ? [info.costOffsets] : []),
            fileList: JSON.stringify(info.fileList ? info.fileList : [])
        }
    }
